import { defineComponent } from 'vue';
import { Editor, EditorContent } from '@tiptap/vue-2';
import Document from '@tiptap/extension-document';
import Heading from '@tiptap/extension-heading';
import Paragraph from '@tiptap/extension-paragraph';
import Text from '@tiptap/extension-text';
import StarterKit from '@tiptap/starter-kit';
import Typography from '@tiptap/extension-typography';
import Highlight from '@tiptap/extension-highlight';
import TaskItem from '@tiptap/extension-task-item';
import TaskList from '@tiptap/extension-task-list';
import FileHandler from '@tiptap-pro/extension-file-handler';
import Image from '@tiptap/extension-image';
export default defineComponent({
  components: {
    EditorContent
  },
  data() {
    return {
      editor: null
    };
  },
  mounted() {
    this.initializeEditor();
    this.loadContent();
  },
  methods: {
    initializeEditor() {
      this.editor = new Editor({
        extensions: [Document, Heading, Paragraph, Text, StarterKit, Typography, Highlight, TaskItem, TaskList, Image, FileHandler.configure({
          allowedMimeTypes: ['image/png', 'image/jpeg', 'image/gif', 'image/webp'],
          onDrop: this.handleFileDrop,
          onPaste: this.handleFilePaste
        })],
        content: '<h1>Try to paste or drop files into this editor</h1>',
        onBlur: () => this.saveContent()
      });
    },
    saveContent() {
      const content = this.editor.getHTML();
      chrome.storage.local.set({
        editorContent: content
      }, () => {
        console.log('Content saved!');
      });
    },
    loadContent() {
      chrome.storage.local.get('editorContent', data => {
        const savedContent = data.editorContent;
        if (savedContent) {
          this.editor.commands.setContent(savedContent);
        }
      });

      // Carregar imagens
      chrome.storage.local.get(null, items => {
        const imageKeys = Object.keys(items).filter(key => key !== 'editorContent');
        imageKeys.forEach(key => {
          const base64Image = items[key];
          this.editor.chain().insertContentAt('end', {
            type: 'image',
            attrs: {
              src: base64Image
            }
          }).run();
        });
      });
    },
    handleFileDrop(currentEditor, files, pos) {
      files.forEach(file => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);
        fileReader.onload = () => {
          const base64Image = fileReader.result;
          // Salvar a imagem no chrome.storage.local
          chrome.storage.local.set({
            [file.name]: base64Image
          }, () => {
            // Inserir a imagem no editor
            currentEditor.chain().insertContentAt(pos, {
              type: 'image',
              attrs: {
                src: base64Image
              }
            }).focus().run();
          });
        };
      });
    },
    handleFilePaste(currentEditor, files) {
      files.forEach(file => {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(file);
        fileReader.onload = () => {
          const base64Image = fileReader.result;
          // Salvar a imagem no chrome.storage.local
          chrome.storage.local.set({
            [file.name]: base64Image
          }, () => {
            // Inserir a imagem no editor
            currentEditor.chain().insertContentAt(currentEditor.state.selection.anchor, {
              type: 'image',
              attrs: {
                src: base64Image
              }
            }).focus().run();
          });
        };
      });
    }
  },
  beforeUnmount() {
    this.editor.destroy();
  }
});
