new Vue({
  el: '#app',
  render: h => h(App),
  created() {
    // Carregar notas salvas ao iniciar a aplicação
    chrome.runtime.sendMessage({
      action: 'getNotes'
    }, response => {
      if (response && response.notes) {
        console.log('Received notes:', response.notes);
        // Faça algo com as notas recebidas ao carregar a aplicação
      }
    });
  },

  methods: {
    saveNotes(editor) {
      // Enviar notas para o armazenamento local do Chrome
      const notes = editor.getHTML();
      chrome.runtime.sendMessage({
        action: 'saveNotes',
        notes
      }, response => {
        if (response && response.status === 'Notes saved') {
          console.log('Notes saved successfully');
        }
      });
    }
  }
});
