import { defineComponent } from 'vue';
import { Editor, EditorContent } from '@tiptap/vue-2';
import Document from '@tiptap/extension-document';
import Placeholder from '@tiptap/extension-placeholder';
import Heading from '@tiptap/extension-heading';
import StarterKit from '@tiptap/starter-kit';
import Typography from '@tiptap/extension-typography';
import Highlight from '@tiptap/extension-highlight';
import TaskItem from '@tiptap/extension-task-item';
import TaskList from '@tiptap/extension-task-list';
import FileHandler from '@tiptap-pro/extension-file-handler';
import Image from '@tiptap/extension-image';
const CustomDocument = Document.extend({
  content: 'heading block*'
});
export default defineComponent({
  components: {
    EditorContent
  },
  data() {
    return {
      editor: null,
      isFirstHeadingEditing: false
    };
  },
  mounted() {
    this.initializeEditor();
    this.loadContent();
  },
  methods: {
    initializeEditor(ids) {
      this.editor = new Editor({
        extensions: [CustomDocument, StarterKit.configure({
          document: false,
          heading: false
        }), Heading.configure({
          HTMLAttributes: {
            class: 'first-heading'
          }
        }), Placeholder.configure({
          placeholder: ({
            node
          }) => {
            if (node.type.name === 'heading') {
              return 'What’s the title?';
            }
            return 'Can you add some further context?';
          }
        }), Typography, Highlight, TaskItem, TaskList, Image.configure({
          inline: true,
          allowBase64: true
        }), FileHandler.configure({
          allowedMimeTypes: ['image/png', 'image/jpeg', 'image/gif', 'image/webp'],
          onDrop: (currentEditor, files, pos) => {
            files.forEach(file => {
              const fileReader = new FileReader();
              fileReader.readAsDataURL(file);
              fileReader.onload = () => {
                currentEditor.chain().insertContentAt(pos, {
                  type: 'image',
                  attrs: {
                    src: fileReader.result
                  }
                }).focus().run();
              };
            });
          },
          onPaste: (currentEditor, files) => {
            files.forEach(file => {
              const fileReader = new FileReader();
              fileReader.readAsDataURL(file);
              fileReader.onload = () => {
                currentEditor.chain().insertContentAt(currentEditor.state.selection.anchor, {
                  type: 'image',
                  attrs: {
                    src: fileReader.result
                  }
                }).focus().run();
              };
            });
          }
        })],
        content: localStorage.getItem('editorContent') || `
          <h1>
            What’s the title?
          </h1>
          <p></p>
          <p></p>
          <p></p>
          <p></p>
          <p></p>
        `,
        onBlur: () => this.saveContent()
        // onUpdate: () => {
        //   const heading = this.editor.view.dom.querySelector('.first-heading')
        //   if (heading) {
        //     console.log(heading)
        //   }
        // }
      });
    },

    // Procurar pelo primeiro h1 e adicionar um ID se encontrado
    addIdToFirstH1() {
      const view = this.editor.view;
      const h1 = view.dom.querySelector('h1');
      if (h1 && !h1.id) {
        h1.id = 'first-h1-id';
      }
    },
    saveContent() {
      const content = this.editor.getHTML();
      localStorage.setItem('editorContent', content);
      this.$emit('notaAtualizada', content);
    },
    loadContent() {
      const savedContent = localStorage.getItem('editorContent');
      if (savedContent) {
        this.editor.commands.setContent(savedContent);
      }
    },
    loadNoteContent(nota) {
      this.editor.commands.setContent(nota.content);
      localStorage.setItem('editorContent', nota.content);
      console.log(nota.title);
      if (nota.length === 0) {
        localStorage.removeItem('editorContent');
      }
    }
  },
  beforeUnmount() {
    this.editor.destroy();
  }
});
