import Tiptap from '../components/tiptap.vue';
import SidebarMenu from '../components/sidebar.vue';
export default {
  name: 'App',
  components: {
    SidebarMenu,
    Tiptap
  },
  methods: {
    criarEditorTiptap(novaNota) {
      // Aqui você pode criar um novo editor Tiptap usando uma referência ao componente Tiptap
      // Vou mostrar um exemplo básico aqui, você pode ajustá-lo conforme necessário
      this.$refs.tiptapEditor.editor.destroy(); // Destruir editor existente (se houver)
      this.$refs.tiptapEditor.initializeEditor(); // Inicializar novo editor
      this.$refs.tiptapEditor.loadNoteContent(novaNota);
    },
    carregarNotaTiptap(nota) {
      this.$refs.tiptapEditor.loadNoteContent(nota);
    },
    AtualziaNotaTiptap(content) {
      const ultimaNotaSelecionada = this.$refs.Sidebar.ultimaNotaSelecionada;
      if (ultimaNotaSelecionada) {
        ultimaNotaSelecionada.content = content;
        console.log('Conteúdo atualizado:', content);
        // Se necessário, você pode salvar a nota atualizada no localStorage ou realizar outras ações aqui
      } else {
        console.error('Nenhuma nota selecionada para atualizar.');
      }
    }
  }
};
