    console.log('A aba foi ativada novamente');
    // Por exemplo, recarregue a página do seu plugin
    chrome.tabs.query({
      active: true,
      currentWindow: true
    }, tabs => {
      if (tabs.length > 0) {
        chrome.tabs.reload(tabs[0].id);
      }
    });
  }
